import { ASTVisitor } from 'graphql-upload/node_modules/graphql/language/visitor';
import { ValidationContext, SDLValidationContext } from 'graphql-upload/node_modules/graphql/validation/ValidationContext';

/**
 * Provided required arguments
 *
 * A field or directive is only valid if all required (non-null without a
 * default value) field arguments have been provided.
 */
export function ProvidedRequiredArgumentsRule(
  context: ValidationContext,
): ASTVisitor;

/**
 * @internal
 */
export function ProvidedRequiredArgumentsOnDirectivesRule(
  context: ValidationContext | SDLValidationContext,
): ASTVisitor;
